(function($) {
    "use strict";

    // Bật hoặc tắt thanh điều hướng bên
    $("#sidebarToggle, #sidebarToggleTop").on("click", function(e) {
        $("body").toggleClass("sidebar-toggled");
        $(".sidebar").toggleClass("toggled");
        if ($(".sidebar").hasClass("toggled")) {
            $(".sidebar .collapse").collapse("hide");
        }
    });

    // Đóng tất cả các menu đã mở khi cửa sổ thay đổi kích thước dưới 768px
    $(window).resize(function() {
        if ($(window).width() < 768) {
            $(".sidebar .collapse").collapse("hide");
        }

        // Ẩn thanh điều hướng bên nếu chiều rộng cửa sổ dưới 480px và chưa bị ẩn
        if ($(window).width() < 480 && !$(".sidebar").hasClass("toggled")) {
            $("body").addClass("sidebar-toggled");
            $(".sidebar").addClass("toggled");
            $(".sidebar .collapse").collapse("hide");
        }
    });

    // Ngăn nội dung bị cuộn khi thanh điều hướng bên cố định được di chuột qua
    $("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function(e) {
        if ($(window).width() > 768) {
            var eOriginal = e.originalEvent;
            var delta = eOriginal.wheelDelta || -eOriginal.detail;
            this.scrollTop += (delta < 0 ? 1 : -1) * 30;
            e.preventDefault();
        }
    });

    // Hiển thị nút "cuộn lên đầu" khi cuộn xuống hơn 100px
    $(document).on("scroll", function() {
        if ($(this).scrollTop() > 100) {
            $(".scroll-to-top").fadeIn();
        } else {
            $(".scroll-to-top").fadeOut();
        }
    });

    // Cuộn mượt mà khi sử dụng jQuery easing
    $(document).on("click", "a.scroll-to-top", function(e) {
        var anchor = $(this);
        $("html, body").stop().animate({
            scrollTop: $(anchor.attr("href")).offset().top
        }, 1000, "easeInOutExpo");
        e.preventDefault();
    });

})(jQuery);
